    if *serverMode {

        log.Println("starting server on port 8080")
        http.HandleFunc("/", GetAdjectiveAnimal)

        log.Fatal(http.ListenAndServe(":8080", nil))
    } else {
        fmt.Printf("%s %s\n", GetAdjective(), GetAnimal())
    }
